import React, { useState } from 'react';
import { signAll } from '../api';
import { BarChart, Bar, XAxis, YAxis, Legend, Tooltip } from 'recharts';

export default function SignAll() {
  const [msg, setMsg] = useState("I authorize this transaction.");
  const [data, setData] = useState(null);

  const run = async () => {
    const r = await signAll(msg);
    setData(r.data.results.map(x=>({
      name: x.algorithm,
      time: x.time,
      size: x.size
    })));
  };

  return (
    <div>
      <textarea value={msg} onChange={e=>setMsg(e.target.value)} rows={4} cols={50}/>
      <br />
      <button onClick={run}>Sign All</button>
      {data &&
        <BarChart width={600} height={300} data={data}>
          <XAxis dataKey="name"/>
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar dataKey="time" fill="#8884d8" name="Time (s)" />
          <Bar dataKey="size" fill="#82ca9d" name="Size (B)" />
        </BarChart>
      }
    </div>
  );
}
