// src/App.js

import React, { useState, useEffect } from 'react';
import NavBar from './components/NavBar';
import KeyGen from './components/KeyGen';
import EncryptAll from './components/EncryptAll';
import SignAll from './components/SignAll';
import History from './components/History';
import Home from './components/Home';
import { getHealth } from './api';

export default function App() {
  const [page, setPage] = useState('Home');

  useEffect(() => {
    getHealth().catch(() => alert('⚠️ Backend not responding on http://localhost:5000'));
  }, []);

  return (
    <div style={{ padding: 20 }}>
      <NavBar selected={page} onNav={setPage} />
      
      {page === 'Home' && <Home onStart={() => setPage('Encrypt All')} />}
      {page === 'Key Generation' && <KeyGen />}
      {page === 'Encrypt All' && <EncryptAll />}
      {page === 'Sign All' && <SignAll />}
      {page === 'History' && <History />}
    </div>
  );
}
